BEGIN
	SET NOCOUNT ON;

	INSERT INTO [Auth].[InvitationSubRole] ([InvitationId], [SubscriptionId], [ProductRoleId])
	VALUES (@InvitationId, @SubscriptionId, @ProductRoleId);

	SELECT SCOPE_IDENTITY();
