CREATE PROCEDURE [Auth].[CreateInvitationSubRole]
	@InvitationId INT,
	@SubscriptionId INT,
	@ProductRoleId INT
AS

BEGIN
    SET NOCOUNT ON;
    SET XACT_ABORT ON;

    BEGIN TRANSACTION
        INSERT INTO [Auth].[Organization] 
                ([Name], 
                [SiteUrl], 
                [Address], 
                [City], 
                [State], 
                [Country], 
                [PostalCode], 
                [PhoneNumber], 
                [FaxNumber], 
                [Subdomain])
        VALUES (@Name,
                @SiteUrl,
                @Address,
                @City, 
                (SELECT [StateId] FROM [Lookup].[State] WITH (NOLOCK) WHERE [Name] = @State), 
                (SELECT [CountryId] FROM [Lookup].[Country] WITH (NOLOCK) WHERE [Name] = @Country),
                @PostalCode,
                @PhoneNumber,
                @FaxNumber,
                @Subdomain);

        SET @retId = SCOPE_IDENTITY();

        EXEC [Auth].[CreateOrgUser] @UserId, @retId, @RoleId;
    COMMIT TRANSACTION

    SELECT SCOPE_IDENTITY();
END

CREATE PROCEDURE [Auth].[CreateOrg]
    @UserId INT,
    @RoleId INT,
    @Name NVARCHAR(100),
    @SiteUrl NVARCHAR(100),
    @Address NVARCHAR(100),
    @City NVARCHAR(100),
    @State NVARCHAR(100),
    @Country NVARCHAR(100),
    @PostalCode NVARCHAR(50),
    @PhoneNumber VARCHAR(50),
    @FaxNumber VARCHAR(50),
    @Subdomain NVARCHAR(40),
    @retId INT OUTPUT
AS

BEGIN SET NOCOUNT ON;
UPDATE [Auth].[OrganizationUser] SET [OrgRoleId] = @RoleId, [EmployeeId] = @EmployeeId WHERE [UserId] = @UserId AND [OrganizationId] = @OrganizationId;
IF @@ROWCOUNT=0 BEGIN INSERT INTO [Auth].[OrganizationUser] ([UserId], [OrganizationId], [OrgRoleId], [EmployeeId]) VALUES (@UserId, @OrganizationId, @RoleId, @EmployeeId); END END

CREATE PROCEDURE [Auth].[CreateOrgUser]
    @UserId INT,
    @OrganizationId INT,
    @RoleId INT,
	@EmployeeId NVARCHAR(128) = NULL
AS

BEGIN
	SET NOCOUNT ON;

	INSERT INTO [Auth].[User] 
		([FirstName], 
		[LastName], 
		[Address], 
		[City], 
		[State], 
		[Country], 
		[PostalCode], 
		[Email], 
		[PhoneNumber], 
		[DateOfBirth],
		[UserName],
		[PasswordHash],
		[EmailConfirmed],
		[TwoFactorEnabled],
		[AccessFailedCount],
		[LockoutEnabled],
		[LockoutEndDateUtc],
		[LanguagePreference])
	VALUES 
		(@FirstName, 
		@LastName, 
		@Address, 
		@City, 
		(SELECT [StateId]	FROM [Lookup].[State] WITH (NOLOCK) WHERE [Name] = @State),
		(SELECT [CountryId] FROM [Lookup].[Country] WITH (NOLOCK) WHERE [Name] = @Country),
		@PostalCode, 
		@Email, 
		@PhoneNumber,
		@DateOfBirth, 
		@Email, 
		@PasswordHash, 
		COALESCE(@EmailConfirmed,0),
		COALESCE(@TwoFactorEnabled,0),
		COALESCE(@AccessFailedCount,0),
		COALESCE(@LockoutEnabled,0),
		COALESCE(@LockoutEndDateUtc,NULL),
		@LanguageID);

	SELECT SCOPE_IDENTITY();
END

CREATE PROCEDURE [Auth].[CreateUserInfo]
	@FirstName NVARCHAR(32),
	@LastName NVARCHAR(32),
    @Address NVARCHAR(100), 
    @City NVARCHAR(32), 
    @State NVARCHAR(32), 
    @Country NVARCHAR(32), 
    @PostalCode NVARCHAR(16),
	@Email NVARCHAR(256), 
    @PhoneNumber VARCHAR(32),
	@DateOfBirth DATETIME2(0),
	@UserName NVARCHAR(256),
	@PasswordHash NVARCHAR(MAX),
	@EmailConfirmed BIT,
	@AccessFailedCount INT,
	@TwoFactorEnabled BIT,
	@LockoutEnabled BIT,
	@LockoutEndDateUtc DATE,
	@LanguageID INT

AS

BEGIN
	SET NOCOUNT ON;

	INSERT INTO [Auth].[Invitation] 
		([Email], 
		[FirstName], 
		[LastName], 
		[DateOfBirth], 
		[OrganizationId], 
		[AccessCode], 
		[IsActive], 
		[OrgRole], 
		[ProjectId])
	VALUES 
		(@Email, 
		@FirstName, 
		@LastName, 
		@DateOfBirth, 
		@OrganizationId, 
		@AccessCode, 
		1, 
		@OrgRole, 
		@ProjectId);

	SET @retId = SCOPE_IDENTITY();

	SELECT SCOPE_IDENTITY();
END

CREATE PROCEDURE [Auth].[CreateUserInvitation]
	@Email NVARCHAR(384),
	@FirstName NVARCHAR(40),
	@LastName NVARCHAR(40),
	@DateOfBirth NVARCHAR(40),
	@OrganizationId INT,
	@AccessCode VARCHAR(50),
	@OrgRole INT,
	@ProjectId INT,
	@retId INT OUTPUT
AS

BEGIN
	SET NOCOUNT ON;

	DELETE FROM [Auth].[InvitationSubRole] 
	WHERE [InvitationId] = @InvitationId 
	AND	[SubscriptionId] = @SubscriptionId;
END

CREATE PROCEDURE [Auth].[DeleteInvitationSubRole]
	@InvitationId INT,
	@SubscriptionId INT
AS

BEGIN BEGIN TRANSACTION
UPDATE [Auth].[Organization] SET [IsActive] = 0, [Subdomain] = NULL WHERE [OrganizationId] = @OrgId;
UPDATE [Billing].[Subscription] SET [IsActive] = 0 WHERE [OrganizationId] = @OrgId;
UPDATE [Crm].[Customer] SET [IsActive] = 0 WHERE [OrganizationId] = @OrgId;
UPDATE [Billing].[StripeCustomerSubscriptionPlan] SET [IsActive] = 0 WHERE [OrganizationId] = @OrgId;
UPDATE [Crm].[Project] SET [IsActive] = 0 WHERE (SELECT [OrganizationId] FROM [Crm].[Customer] WITH (NOLOCK) WHERE [Customer].[CustomerId] = [Project].[CustomerId]) = @OrgId;
UPDATE [Crm].[ProjectUser] SET [IsActive] = 0 WHERE [ProjectUser].[ProjectId] IN (SELECT [ProjectId] FROM [Crm].[Project] WITH (NOLOCK) WHERE [IsActive] = 0);
UPDATE [Billing].[StripeOrganizationCustomer] SET [IsActive] = 0 WHERE [OrganizationId] = @OrgId;
UPDATE [Auth].[Invitation] SET [IsActive] = 0 WHERE [OrganizationId] = @OrgId;
COMMIT TRANSACTION END

CREATE PROCEDURE [Auth].[DeleteOrg]
	@OrgId INT
AS

BEGIN
	SET NOCOUNT ON;

	DELETE FROM [Crm].[ProjectUser] WHERE [UserId] = @UserId AND [ProjectId] IN 
		(SELECT [ProjectId] FROM [Crm].[Project] WHERE [CustomerId] IN
			(SELECT [CustomerId] FROM [Crm].[Customer] WHERE [OrganizationId] = @OrganizationId));

	DELETE FROM [Billing].[SubscriptionUser] WHERE [UserId] = @UserId AND [SubscriptionId] IN 
		(SELECT [SubscriptionId] FROM [Billing].[Subscription] WHERE [OrganizationId] = @OrganizationId);

	DELETE FROM [Auth].[OrganizationUser] WHERE [OrganizationUser].[UserId] = @UserId;


	--DECLARE @SubscriptionId INT = 
