	--	FROM [Billing].[Subscription] 
	--	WITH (NOLOCK)
	--	WHERE [OrganizationId] = @OrganizationId 
	--	ORDER BY [SubscriptionId] DESC);
	--DELETE FROM [Billing].[SubscriptionUser] WHERE [SubscriptionId] = @SubscriptionId AND [UserId] = @UserId
END

