CREATE PROCEDURE [Auth].[GetUsersWithSubscriptionToProductInOrganization]
	@OrganizationId INT,
	@ProductId INT 
AS

BEGIN
    UPDATE [Auth].[Invitation] SET [ModifiedUTC] = CONVERT(DATETIME2(0), GETUTCDATE()) FROM [Auth].[Invitation] INNER JOIN [deleted] [d] ON [Invitation].[InvitationId] = [d].[InvitationId];
END

BEGIN
    UPDATE [Auth].[InvitationSubRole] SET [ModifiedUTC] = CONVERT(DATETIME2(0), GETUTCDATE()) FROM [Auth].[InvitationSubRole] INNER JOIN [deleted] [d] ON [InvitationSubRole].[InvitationId] = [d].[InvitationId];
END

BEGIN
	UPDATE [Auth].[Organization] SET [ModifiedUTC] = CONVERT(DATETIME2(0), GETUTCDATE()) FROM [Auth].[Organization] INNER JOIN [deleted] [d] ON [Organization].[OrganizationId] = [d].[OrganizationId];
END

BEGIN
    UPDATE [Auth].[OrganizationUser] SET [ModifiedUTC] = CONVERT(DATETIME2(0), GETUTCDATE()) FROM [Auth].[OrganizationUser] INNER JOIN [deleted] [d] ON [OrganizationUser].[OrganizationId] = [d].[OrganizationId] AND [OrganizationUser].[UserId] = [d].[UserId];
END

BEGIN
	UPDATE [Auth].[User] SET [ModifiedUtc] = CONVERT(DATETIME2(0), GETUTCDATE()) FROM [Auth].[User] INNER JOIN [deleted] [d] ON [User].[UserId] = [d].[UserId];
END

