CREATE PROCEDURE [Crm].[CreateProjectFromCustomerIdOnly]
	@CustomerId INT,
	@Name NVARCHAR(MAX),
	@PriceType NVARCHAR(20),
	@StartingDate DATETIME2(0),
	@EndingDate DATETIME2(0),
	@retId INT OUTPUT
AS

BEGIN 
	SET NOCOUNT ON;
	UPDATE [Crm].[ProjectUser] SET [IsActive] = 1
	WHERE [ProjectId] = @ProjectId AND [UserId] = @UserId;
	IF @@ROWCOUNT < 1
		INSERT INTO [Crm].[ProjectUser] ([ProjectId], [UserId], [IsActive])
		VALUES(@ProjectId, @UserId, 1);
END

CREATE PROCEDURE [Crm].[CreateProjectUser]
	@ProjectId INT,
	@UserId INT
AS

BEGIN
	SET NOCOUNT ON;
	UPDATE [Crm].[Customer] SET [IsActive] = 0
	WHERE [Customer].[CustomerId] = @CustomerId;

	UPDATE [Crm].[Project] SET [IsActive] = 0
	WHERE [Project].[CustomerId] IN (SELECT [CustomerId] FROM [Crm].[Customer] WHERE [IsActive] = 0);
END

