CREATE PROCEDURE [TimeTracker].[CreateTimeEntry]
	@UserId INT,
	@ProjectId INT,
	@PayClassId INT,
    @Date DATE,
    @Duration FLOAT,
    @Description NVARCHAR(120)
AS

SET NOCOUNT ON;
DELETE FROM [TimeTracker].[Holiday] WHERE [HolidayName] = @HolidayName AND [Date] = @Date AND [OrganizationId] = @OrganizationId;
BEGIN DELETE FROM [TimeTracker].[TimeEntry] WHERE [Date] = @Date AND [Duration] = 8 AND [PayClassId] = (SELECT TOP 1 [PayClassID] FROM [PayClass] WITH (NOLOCK) WHERE [Name] = 'Holiday') AND [ProjectId] IN (SELECT [ProjectId] FROM [Crm].[Project] WITH (NOLOCK) JOIN [Crm].[Customer] WITH (NOLOCK) ON [Customer].[CustomerId] = [Project].[CustomerId] WHERE [Customer].[OrganizationId] = @OrganizationId); END

