BEGIN
UPDATE [TimeTracker].[PayClass] SET [ModifiedUTC] = CONVERT(DATETIME2(0), GETUTCDATE()) FROM [TimeTracker].[PayClass] INNER JOIN [deleted] [d] ON [PayClass].PayClassID = [d].PayClassID;
END

